#include <algorithm>
#include <vector>
#include <queue>
#include <stack>
#include <map>
#include <set>

typedef long long ll;

using namespace std;

long long h,w,ans,flag=0;
char m[1020][1020];
bool mc[1020][1020];

int check(int y,int x){
    int c=0;

    for(int i=-1;i<=1;i++)
        for(int j=-1;j<=1;j++)
            if(m[y+i][x+j]=='.') c++;

    if((int)(m[y][x]-'0')<=c){
        mc[y][x]=true;
        return 1;
    }

    return 0;
}

int main(){
    scanf("%lld%lld",&h,&w);

    for(int i=0;i<h;i++)
        scanf("%s",m[i]);

    do{
        flag=0;

        for(int i=0;i<h;i++)
            for(int j=0;j<w;j++)
                mc[i][j]=false;

        for(int i=1;i<h-1;i++)
            for(int j=1;j<w-1;j++)
                if(m[i][j]!='.') flag+=check(i,j);

        for(int i=0;i<h;i++)
            for(int j=0;j<w;j++)
                if(mc[i][j]) m[i][j]='.';

        if(flag) ans++;

    }while(flag);

    printf("%lld\n",ans);

    return 0;
}

