typedef pair<int,int>P;

char s[1000][1001];
int d[1000][1000],cnt[1000][1000];
int main(){
	int h,w;scanf("%d%d",&h,&w);
	rep(i,h)scanf("%s",s[i]);
	int Max=0;
	queue<P>que;
	rep(i,h)rep(j,w){
		if(s[i][j]=='.')continue;
		for(int k=-1;k<=1;k++){
			for(int l=-1;l<=1;l++){
				int x=i+k,y=j+l;
				if(0<=x&&x<h&&0<=y&&y<w&&s[x][y]=='.')cnt[i][j]++;
			}
		}
		if(cnt[i][j]>=s[i][j]-'0'){
			d[i][j]=Max=1;que.push(P(i,j));
		}
	}
	while(!que.empty()){
		P p=que.front();que.pop();
		for(int i=-1;i<=1;i++)for(int j=-1;j<=1;j++){
			int x=p.first+i,y=p.second+j;
			if(0<=x&&x<h&&0<=y&&y<w&&s[x][y]!='.'&&!d[x][y]){
				if(++cnt[x][y]>=s[x][y]-'0'){
					d[x][y]=Max=d[p.first][p.second]+1;que.push(P(x,y));
				}
			}
		}
	}
	printf("%d\n",Max);
}

