#define for_(i,a,b) for(int i=(a);i<(b);++i)
#define size_of(a) (int)(a).size()

typedef pair< int, int > pii;

const int dx[8] = {0,1,1,1,0,-1,-1,-1}, dy[8] = {-1,-1,0,1,1,1,0,-1};

const int MASK = (1 << 15) - 1;
const int SHIFT = 15;

int H, W, counter[1010][1010];
string grid[1010];

int main() {
	cin >> H >> W;
	for_(i,0,H) cin >> grid[i];

	queue< pii > cur_que;

	for_(y,0,H) for_(x,0,W) {
		counter[y][x] = 0;
		if (grid[y][x] == '.') continue;
		counter[y][x] = grid[y][x] - '0';

		for_(d,0,8) {
			int xx = x + dx[d], yy = y + dy[d];
			if (grid[yy][xx] == '.') --counter[y][x];
		}

		if (counter[y][x] == 0) cur_que.push(pii(x, y));
	}

	queue< pii > nxt_que;

	int ans = 0;

	while (!cur_que.empty()) {
		while (!cur_que.empty()) {
			pii p = cur_que.front(); cur_que.pop();
			int x = p.first, y = p.second;

			for_(d,0,8) {
				int xx = x + dx[d], yy = y + dy[d];
				--counter[yy][xx];
				if (counter[yy][xx] == 0) nxt_que.push(pii(xx, yy));
			}
		}

		++ans;
		swap(cur_que, nxt_que);
	}

	cout << ans << endl;
}

#include <cstring>
#include <iostream>
#include <algorithm>

using namespace std;

#define INF 2000000000

#define MAX_H 1050
#define MAX_W 1050

int H, W;

char sand[MAX_H][MAX_W];

int dp[MAX_H][MAX_W];

const int dx[8] = { -1, -1, -1, 0, 0, 1, 1, 1 };
const int dy[8] = { -1, 0, 1, -1, 1, -1, 0, 1 };

bool dfs[MAX_H][MAX_W];

void memo(int x, int y)
{
	dfs[y][x] = true;

	if (dp[y][x] > 1 && x > 0 && x < W - 1 && y > 0 && y < H - 1)
	{
		int temp[8];

		for (int dir = 0; dir < 8; dir++)
		{
			temp[dir] = dp[y + dy[dir]][x + dx[dir]];
		}

		sort(temp, temp + 8);

		if (sand[y][x] != '9')
		{
			if (temp[sand[y][x] - 49] != INF)
			{
				dp[y][x] = temp[sand[y][x] - 49] + 1;
			}

			if (dp[y][x] < INF)
			{
				for (int dir = 0; dir < 8; dir++)
				{
					if (dfs[y + dy[dir]][x + dx[dir]] == false)
					{
						memo(x + dx[dir], y + dy[dir]);
					}
				}
			}
		}
	}
}

int main()
{
	memset(dp, 0, sizeof(dp));

	cin >> H >> W;

	for (int i = 0; i < H; i++)
	{
		for (int j = 0; j < W; j++)
		{
			cin >> sand[i][j];

			if (sand[i][j] != '.')
			{
				dp[i][j] = INF;
			}
		}
	}

	for (int i = 1; i < H - 1; i++)
	{
		for (int j = 1; j < W - 1; j++)
		{
		    for(int i1 = 0; i1 < H; i1++)
		    {
		        for(int j1 = 0; j1 < W; j1++)
		        {
		            if(dfs[i1][j1] == true)
		            {
		                dfs[i1][j1] = false;
		            }
		        }
		    }

			memo(j, i);
		}
	}

	int maxdepth = 0;

	for (int i = 1; i < H - 1; i++)
	{
		for (int j = 1; j < W - 1; j++)
		{
			if (dp[i][j] < INF)
			{
				maxdepth = max(dp[i][j], maxdepth);
			}
		}
	}

	cout << maxdepth << endl;

	return 0;
}

