					if (x[c + i][d + j] >= 1) {
						p = 0;
						for (int k = -1; k <= 1; k++) {
							for (int l = -1; l <= 1; l++) {
								if (x[c + i + k][d + j + l]) {
									p++;
								}
							}
						}
						if (p >= x[c + i][d + j] && x[c + i][d + j] != 10) {
							x[c + i][d + j] = 10;
							R.push((c + i) * 1000 + (d + j));
						}
					}
				}
			}
			Q.pop();
		}
		while (!R.empty()) {
			c = R.front() / 1000;
			d = R.front() % 1000;
			x[c][d] = 0;
			Q.push(R.front());
			R.pop();
		}
	}

	cout << sum << endl;
	return 0;
}

