	for (int i = 0; i < h; i++)
	{
		scanf("%s", c);
		for (int j = 0; j < w; j++)
		{

			if (c[j] == '.')
				sc[i][j] = -1;
			else
				sc[i][j] = c[j] - 48;
		}
	}
	for (int i = 1; i < h; i++)
	{
		for (int j = 1; j < w; j++)
		{
			if (sc[i][j] != -1)
			{
				for (int a = i - 1; a < i + 2; a++)
				{
					for (int b = j - 1; b < j + 2; b++)
					{
						if (sc[a][b] == -1)ssc[i][j] ++;
					}
				}
				if (sc[i][j] <= ssc[i][j])
					que.push(MP(i, j));
				else if (sc[i][j] != 9)
					que1.push(MP(i, j));
			}
		}
