	while (!que.empty())
	{
		while (!que.empty())
		{
			x = que.front().first;
			y = que.front().second;
			que.pop();
			for (int a = -1; a < 2; a++)
			{
				ssc[x - 1][y + a]++;
				ssc[x + 1][y + a]++;
			}
			ssc[x][y - 1]++;
			ssc[x][y + 1]++;
		}
		n = que1.size();
