	vector<P> lis[2];

	for (int i = 0;i < h;i++){
		for (int j = 0;j < w;j++){
			if(field[i][j] >= '1' && field[i][j] <= '9'){
				for (int k = 0;k < 8;k++){
					int ni = i+dx[k], nj = j+dy[k];
					if(field[ni][nj] == '.') cnt[i][j]++;
				}
				if(cnt[i][j] >= field[i][j]-'0') lis[0].push_back(P(i,j));
			}
		}
