using namespace std;

int H, W, snd[1001][1001], ans = 0, scnt = 0;
queue<pair<int, int>> q;

bool Is_inmap(int i, int j) {
	bool jud = true;
	if (i < 0 || j < 0) jud = false;
	if (i >= H || j >= W) jud = false;
	return jud;
}

int main() {
	//input
	cin >> H >> W;
	for (int i = 0; i < H; i++) {
		for (int j = 0; j < W; j++) {
			char C;  cin >> C;
			if (C == '.') {
				snd[i][j] = -1;
				q.push({ i, j });
			}
			else snd[i][j] = C - 48;
		}
	}
	q.push({ -1, -1 });
	while (!q.empty()) {
		pair<int, int> tp = q.front(); q.pop();
		int I = tp.first, J = tp.second; 
		if(I == -1 && J == -1){
			/*
			cout << endl << "ans = " << ans << endl;
			for (int i = 0; i < H; i++) {
				for (int j = 0; j < W; j++) {
					cout << (snd[i][j] == -1 ? 0 : snd[i][j]);
				}
				cout << endl;
			}
			cout << endl;
			*/
			if (scnt) {
				scnt = 0;
				q.push({ -1, -1 });//new wave
			}
			else break;//no change
			ans++;//end of wave
		}
		for (int i = -1; i <= 1; i++) {
			for (int j = -1; j <= 1; j++) {
				int ni = I + i, nj = J + j;
				if (!Is_inmap(ni, nj)) continue;
				if (snd[ni][nj] > 0) {
					snd[ni][nj]--;
					if (snd[ni][nj] == 0) {
						snd[ni][nj]--;
						scnt++;
						q.push({ ni, nj });
					}
				}
			}
		}
	}
	cout << ans << endl;
	return 0;
}

