			for(int s : {-1, 0, 1}) for(int t : {-1, 0, 1})
				cnt[i][j] += (c[i + s][j + t] == '.');
			if(cnt[i][j] >= c[i][j] - '0')
				v.pb({i, j});
		}
	int ans = 0;
	while(v.size()) {
		vector <pii> u;
		for(pii p : v)
			for(int s : {-1, 0, 1}) for(int t : {-1, 0, 1}) {
				int i = p.fi + s, j = p.se + t;
				cnt[i][j] ++;
				if(c[i][j] != '.' && cnt[i][j] == c[i][j] - '0')
					u.pb({i, j});
			}
		v = u;
		ans ++;
	}
	cout << ans << '\n';
}

