int dx[] = { 0, 1, 1, 1, 0, -1, -1, -1 };
int dy[] = { 1, 1, 0, -1, -1, -1, 0, 1 };
int H, W, dist[1009][1009]; string s[1009];
int main() {
	ios::sync_with_stdio(false);
	cin >> H >> W;
	priority_queue<state> que;
	for (int i = 0; i < H; i++) {
		cin >> s[i];
		for (int j = 0; j < W; j++) {
			if (s[i][j] == '.') {
				dist[i][j] = 0;
				que.push(state{ i, j, 0 });
			}
			else dist[i][j] = 1012345678;
		}
	}
	int ret = 0;
	while (!que.empty()) {
		state u = que.top(); que.pop();
		ret = max(ret, dist[u.x][u.y]);
		for (int i = 0; i < 8; i++) {
			int tx = u.x + dx[i], ty = u.y + dy[i];
			if (0 <= tx && tx < H && 0 <= ty && ty < W) {
				vector<int> v;
