void bfs() {
	queue<P>que;
	queue<P>que2;
	for (int i = 0; i < h; i++) {
		for (int j = 0; j < w; j++) {
			if (f[i][j]) {
				for (int k = -1; k <= 1; k++) {
					for (int l = -1; l <= 1; l++) {
						if (f[i + k][j + l] == 0) cnt[i][j]++;
					}
				}
				if (cnt[i][j] >= f[i][j]) {
					que.push(P(i, j));
					t[i][j] = 1;
					que2.push(P(i, j));
				}
			}
			else cnt[i][j] = -1;
		}
	}
	while (!que2.empty()) {
		P p = que2.front();
		que2.pop();
		f[p.first][p.second] = 0;
	}
