				cnt[nx][ny]++;
				if (f[nx][ny]) {
					if (cnt[nx][ny] == f[nx][ny]) {
						que2.push(P(nx, ny));
						t[nx][ny] = t[p.first][p.second] + 1;
						res = max(res, t[nx][ny]);
