using namespace std;
typedef pair<int, int> P;

int h, w, ans;
int castle[1001][1001], times[1001][1001];
int dx[] = {-1, 0, 1, -1, 1, -1, 0, 1}, dy[] = {-1, -1, -1, 0, 0, 1, 1, 1};
char beach[1001][1001];

inline int input_i(){
	int temp;
	scanf("%d", &temp);
	return temp;
}

inline char input_c(){
	char temp;
	scanf("%c", &temp);
	return temp;
}


int main(){
    h = input_i();
    w = input_i();
    for(int i = 0; i < h; i++){
        beach[i] = input_c();
    }

    queue<P> Q;
    for(int i = 0; i < h; i++){
        for(int j = 0; j < w; j++){
            if(beach[i][j] == '.'){
                Q.push(P(i, j));
            } else{
                castle[i][j] = beach[i][j] - '0';
            }
        }
    }

    while(Q.size()){
        P p = Q.front();
        Q.pop();
        ans = max(ans, times[p.first][p.second]);

        for(int i = 0; i < 8; i++){
            int y = p.first + dy[i], x = p.second + dx[i];
            if(y < 0 || y >= h || x < 0 || x >= w){
                continue;
            }

            castle[y][x]--;
            if(castle[y][x] == 0){
                Q.push(P(y, x));
                times[y][x] = times[p.first][p.second] + 1;
            }
        }
    }

    printf("%d\n", ans);

    return 0;
}

