    }
    return ret;
}

int main(){
    scanf("%d%d",&H,&W);
    for(int i=0;i<H;i++)scanf("%s",fld[i]);

    vector<int>V[2];
    for(int i=0;i<H;i++){
        for(int j=0;j<W;j++){
            if(fld[i][j]=='.')continue;
            if(f(i*W+j)>=(fld[i][j]-'0')){
                V[0].push_back(i*W+j);
                used[i][j]=true;
            }
        }
    }

    for(int i=0;;i++){
        if(V[i&1].size()==0){
            printf("%d\n",i);
            break;
        }
        for(int j=0;j<V[i&1].size();j++){
            int y=V[i&1][j]/W,x=V[i&1][j]%W;
            fld[y][x]='.';
            for(int d=0;d<8;d++){
                int ty=y+dy[d],tx=x+dx[d];
                if(ty<0||ty>=H||tx<0||tx>=W)continue;
                if(used[ty][tx]||fld[ty][tx]=='.')continue;
                if(f(ty*W+tx)>=(fld[ty][tx]-'0')){
                    used[ty][tx]=true;
                    V[(i+1)&1].push_back(ty*W+tx);
                }
            }
        }

        V[i&1].clear();
    }
    return 0;
}

