int height, width;
int beach[1000][1000] = { 0 };

using namespace std;
queue<int> que_i, que_j;

int power(int i, int j) {
	int count = 0;

	if (beach[i - 1][j] == 0) {
		count++;
	}
	if (beach[i][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j] == 0) {
		count++;
	}
	if (beach[i][j - 1] == 0) {
		count++;
	}

	if (beach[i - 1][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j - 1] == 0) {
		count++;
	}
	if (beach[i - 1][j - 1] == 0) {
		count++;
	}

	return count;
}

void around(int i, int j) {
	if (beach[i - 1][j] != 0) {
		if (beach[i - 1][j] <= power(i - 1, j)) {
			que_i.push(i - 1);
			que_j.push(j);
		}
	}
	if (beach[i][j + 1] != 0) {
		if (beach[i][j + 1] <= power(i, j + 1)) {
			que_i.push(i);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j] != 0) {
		if (beach[i + 1][j] <= power(i + 1, j)) {
			que_i.push(i + 1);
			que_j.push(j);
		}
	}
	if (beach[i][j - 1] != 0) {
		if (beach[i][j - 1] <= power(i, j - 1)) {
			que_i.push(i);
			que_j.push(j - 1);
		}
	}

	if (beach[i - 1][j + 1] != 0) {
		if (beach[i - 1][j + 1] <= power(i - 1, j + 1)) {
			que_i.push(i - 1);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j + 1] != 0) {
		if (beach[i + 1][j + 1] <= power(i + 1, j + 1)) {
			que_i.push(i + 1);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j - 1] != 0) {
		if (beach[i + 1][j - 1] <= power(i + 1, j - 1)) {
			que_i.push(i + 1);
			que_j.push(j - 1);
		}
	}
	if (beach[i - 1][j - 1] != 0) {
		if (beach[i - 1][j - 1] <= power(i - 1, j - 1)) {
			que_i.push(i - 1);
			que_j.push(j - 1);
		}
	}
}

int main(void)
{


	scanf("%d %d", &height, &width);

	char c;
	for (int i = 0; i < height; i++) {
		for (int j = 0; j < width; j++) {
			c = getchar();
			if (c == '\n') {
				j--;
				continue;
			}
			else if (c == '.') {
				beach[i][j] = 0;
			}
			else {
				beach[i][j] = c - 48;
			}
		}
	}

	for (int i = 0; i < height; i++) {
		for (int j = 0; j < width; j++) {
			if (beach[i][j] != 0) {
				if (beach[i][j] <= power(i, j)) {
					que_i.push(i);
					que_j.push(j);
				}
			}
		}
	}

	int wave = 0, x[1000], y[1000], count, save, size;
	while (que_i.empty() == false) {
		wave++;
		count = 0;
		size = que_i.size();
		for (int i = 0; i < size; i++) {
			if (beach[que_i.front()][que_j.front()] != 0) {

				beach[que_i.front()][que_j.front()] = 0;
				y[count] = que_i.front();
				x[count] = que_j.front();
				count++;
			}
			que_i.pop();
			que_j.pop();
		}

		for (int i = 0; i < count; i++) {
			around(y[i], x[i]);
		}
	}

	printf("%d\n", wave);

	return 0;
}

