            int p=f[ny][nx]-'0';

            if(dp[ny][nx]>a[p-1]+1)
            {
                dp[ny][nx]=a[p-1]+1;
                que.push(pi(ny,nx));
            }
        }
    }

    int ans=0;
    rep(i,h)rep(j,w) if(dp[i][j]!=INF) ans=max(ans,dp[i][j]);
    cout << ans << endl;
    return 0;
}

