static const int tx[] = {+0,+1,+1,+1,+0,-1,-1,-1};
static const int ty[] = {-1,-1,+0,+1,+1,+1,+0,-1};

int main(){
  int H,W;
  while(~scanf("%d %d",&H,&W)){
    char stage[101][101];
    char buffer[101][101];
    for(int y = 0; y < H; y++){
      char line[101];
      scanf("%s",line);
      for(int x = 0; x < W; x++){
        stage[y][x] = line[x];
        buffer[y][x] = line[x];
      }
    }


    int res = 0;
    for(int round = 0; round < 10010; round++){
      bool is_over = true;
      for(int y = 0; y < H; y++){
        for(int x = 0; x < W; x++){
          if(stage[y][x] == '.') continue;
