            int dx = x + tx[i];
            int dy = y + ty[i];
            if(dx < 0 || dx >= W || dy < 0 || dy >= H) continue;
            if(stage[dy][dx] == '.'){
              count++;
            }
          }

          if(count >= strongness){
            is_over = false;
            buffer[y][x] = '.';
          }
        }
      }
      if(is_over){
        res = round;
        break;
