int ans = 0;

int main(void) {
	scanf("%d%d", &H, &W);
	for (int i = 0; i < H; ++i) { scanf("%s", &sand_before[i]); }

	for (int i = 0; i < H; ++i) { for (int j = 0; j < W; ++j) { sand_after[i][j] = sand_before[i][j]; } }
	while (true) {
		for (int i = 1; i < H - 1; ++i) {
			for (int j = 1; j < W - 1; ++j) {
				if (sand_before[i][j] != '.') {
					int wave = 0;
					for (int x = i - 1; x <= i + 1; ++x) {
						for (int y = j - 1; y <= j + 1; ++y) {
							if (sand_before[x][y] == '.') { wave++; }
						}
					}
					if (sand_before[i][j] - '0' <= wave) { sand_after[i][j] = '.'; broken_cou = true; }
				}
			}
		}
		if (broken_cou == false) { break; }
		for (int i = 0; i < H; ++i) { for (int j = 0; j < W; ++j) { sand_before[i][j] = sand_after[i][j]; } }
		ans++;
