const int dx[8] = {0,1,1,1,0,-1,-1,-1}, dy[8] = {-1,-1,0,1,1,1,0,-1};

int H, W, counter[1010][1010];
string grid[1010];

int main() {
	cin >> H >> W;
	for_(i,0,H) cin >> grid[i];

	queue< pii > cur_que;

	for_(y,0,H) for_(x,0,W) {
		counter[y][x] = -1;
		if (grid[y][x] == '.') continue;
		counter[y][x] = grid[y][x] - '0';

		for_(d,0,8) {
			int xx = x + dx[d], yy = y + dy[d];
			if (grid[yy][xx] == '.') --counter[y][x];
		}

		if (counter[y][x] <= 0) cur_que.push(pii(x, y));
	}

	queue< pii > nxt_que;

	int ans = 0;

	while (!cur_que.empty()) {
		while (!cur_que.empty()) {
			pii p = cur_que.front(); cur_que.pop();
			int x = p.first, y = p.second;
			assert(grid[y][x] != '.');
			grid[y][x] = '.';

			for_(d,0,8) {
				int xx = x + dx[d], yy = y + dy[d];
				if (grid[yy][xx] == '.') continue;
				--counter[yy][xx];
				if (counter[yy][xx] == 0) nxt_que.push(pii(xx, yy));
			}
		}

		++ans;
		swap(cur_que, nxt_que);
	}

	cout << ans << endl;
}

