    cin>>s;

    queue<pii> A,B;
    auto check=[&](int i,int j) {
        if(s[i][j]=='.') return false;
        int cnt=0;
        REP(d,8) {
            int ii=i+DY[d];
            int jj=j+DX[d];
            if(s[ii][jj]=='.') cnt++;
        }
        if(s[i][j]-'0'<=cnt) {
            return true;
        } else {
