int dx[8] = {-1, 0, 1, 1, 1, 0, -1, -1};
int dy[8] = {1, 1, 1, 0, -1, -1, -1, 0};

int w, h, ans = 0;
int state[1001][1001];
int score[1001][1001];
void show(){
	rep(i,h){
		rep(j,w){
			if(state[j][i] == 0)cout << '.';
			else cout << state[j][i];
		}
		cout << endl;
	}
