int main(void){
	cin >> h >> w;
	fill_n(*state, 1001*1001, -1);
	rep(i,h)rep(j,w){
		char c;
		cin >> c;
		if(c == '.')state[j][i] = 0;
		else state[j][i] = c-'0';
	}
	queue<P> que;
	rep(i,h)rep(j,w){
		if(state[j][i] == 0)continue;
		int count = 0;
		rep(k,8){
			int nx = j+dx[k];
			int ny = i+dy[k];
			if(nx < 0 || ny < 0 || state[nx][ny] == -1)continue;
			if(state[nx][ny] == 0)count++;
		}
		score[j][i] = count;
		if(state[j][i] <= count){
			que.push(P(j, i));
		}
	}
	///show();
	while(!que.empty()){
		queue<P> reQue;
		while(!que.empty()){
			reQue.push(que.front());
			///cout << "(" << que.front().first << "," << que.front().second << ")" << endl;
			P p = que.front();
			int x = p.first;
			int y = p.second;
			state[x][y] = 0;
			score[x][y] = 0;
			rep(k,8){
				int nx = x+dx[k];
				int ny = y+dy[k];
				score[nx][ny]++;
			}
			que.pop();
		}
		int memo[1001][1001] = {0};
		while(!reQue.empty()){
			P p = reQue.front();
			reQue.pop();
			int x = p.first;
			int y = p.second;
			///cout << "(" << x << "," << y << ")" << endl;
			rep(k,8){
				int nx = x+dx[k];
				int ny = y+dy[k];
				if(nx < 0 || ny < 0 || state[nx][ny] == -1)continue;
				if(state[nx][ny] == 0)continue;
				if(state[nx][ny] <= score[nx][ny] && !memo[nx][ny]){
					memo[nx][ny]=1;
					que.push(P(nx, ny));
				}
			}
		}
		///show();
		ans++;
	}
	cout << ans << endl;

	return 0;
}



