		scanf("%*c");
		for(int j = 0; w > j; j++){
			char buf;
			scanf("%c", &buf);

			if(buf == '.') buf = '0';
			else {
				if(s[0] == 0){
					s[0] = i; s[1] = j;
				}
				if(e[0] < i || e[1] < j) {
					e[0] = i; e[1] = j;
				}
			}

			map[i][j].num = buf - '0';

		}
	}

	int t = 1, count;
	for(count = 0; t; count++){
		t = 0;
		for(int i = s[0]; e[0] >= i; i++){
			for(int j = s[1]; e[1] >= j; j++){
				if (map[i][j].num){
					int emptyc = 0;
					for (int c = 0; 8 > c; c++){
						if (!map[i + dir[0][c]][j + dir[1][c]].num && !map[i + dir[0][c]][j + dir[1][c]].change) emptyc++;
					}
					if (emptyc >= map[i][j].num){
						t = 1;
						map[i][j].num = 0;
						map[i][j].change = 1;

					}
				}
			}
