struct p{
  int co,x,y;
  p(int a,int b,int c){
    co=a;
    x=b;
    y=c;
  }
};
int a[1000][1000],b[1000][1000];
int main (){
  int h,w;
  queue<p> Q; 
  string s;
  cin >>h>>w;
  for(int i=0;i<h;i++){
    cin >>s;
    for(int j=0;j<w;j++){
      if(s[j] == '.'){
	a[i][j] = -1;
	Q.push(p(0,j,i));
      } else a[i][j] = s[j]-'0';
      b[i][j] = 0;
    }
  }
  int res = 0;
  while( !Q.empty() ){
    p np = Q.front();
    Q.pop();
    if(res < np.co) res = np.co;
    for(int i=-1;i<=1;i++)
      for(int j=-1;j<=1;j++){
	if(i==0 && j==0) continue;
	int nx = np.x+i;
	int ny = np.y+j;
	if(0>=nx || nx>w-1 || 0>=ny || ny>h-1) continue;
	b[nx][ny]++;
	if(a[nx][ny] == b[nx][ny]) Q.push(p(np.co+1,nx,ny));
      }
  }
  cout << res<<endl;
  return 0;
}

