using namespace std;
typedef pair<int, int>P;
typedef long long ll;

int h, w;
char ca[1000][1001];
queue<P>que1;
queue<P>que2;

int cnt(int a, int b){
	int res = 0;
	for (int i = -1; i <= 1; i++){
		for (int j = -1; j <= 1; j++){
			if (ca[a + i][b + j] == '.')
				res++;
		}
	}
	return res;
}
void wave(){
	while (!que1.empty()){
		P p = que1.front(); que1.pop();
		int f = p.first, s = p.second;
		ca[f][s] = '.';
		for (int i = -1; i <= 1; i++){
			for (int j = -1; j <= 1; j++){
				if ('1' <= ca[f + i][s + j] && ca[f + i][s + j] - '0' <= cnt(f + i, s + j))
					que2.push(P(f + i, s + j));
			}
		}
	}
	swap(que1, que2);
}
int main(){
	scanf("%d%d", &h, &w);
	rep(i, h)
		scanf("%s", ca[i]);
	rep(i, h){
		rep(j, w){
			if ('1' <= ca[i][j] && ca[i][j] - '0' <= cnt(i, j))
				que1.push(P(i, j));
		}
	}
	int cnt = 0;
	while (!que1.empty()){
		wave();
		cnt++;
	}
	printf("%d\n", cnt);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

using pii = pair<int, int>;

constexpr int dx[8] = {0, 1, 1, 1, 0, -1, -1, -1};
constexpr int dy[8] = {-1, -1, 0, 1, 1, 1, 0, -1};

int main() {
    int H, W;
    cin >> H >> W;
    vector<string> v(H);
    for(int i = 0; i < H; ++i) {
        cin >> v[i];
    }

    vector<pii> now;
    for(int i = 0; i < H; ++i) {
        for(int j = 0; j < W; ++j) {
            if(v[i][j] == '.') {
                continue;
            }
            for(int k = 0; k < 8; ++k) {
                int ny = i + dy[k];
                int nx = j + dx[k];
                if(ny < 0 || H <= ny || nx < 0 || W <= nx) {
                    continue;
                }
                v[i][j] -= v[ny][nx] == '.';
                v[i][j] = max('0', v[i][j]);
            }
        }
    }
    for(int i = 0; i < H; ++i) {
        for(int j = 0; j < W; ++j) {
            if(v[i][j] == '0') {
                v[i][j] = '.';
                now.emplace_back(i, j);
            }
        }
    }

    int res = 0;
    while(!now.empty()) {
        vector<pii> next;
        for(auto& p : now) {
            for(int i = 0; i < 8; ++i) {
                int ny = p.first + dy[i];
                int nx = p.second + dx[i];
                if(ny < 0 || H <= ny || nx < 0 || W <= nx || v[ny][nx] == '.') {
                    continue;
                }
                v[ny][nx]--;
                v[ny][nx] = max('0', v[ny][nx]);
                if(v[ny][nx] == '0') {
                    v[ny][nx] = '.';
                    next.emplace_back(ny, nx);
                }
            }
        }
        now = move(next);
        res++;
    }

    cout << res << endl;
}

