  static char M[1000][1001];
  for(int i=0;i<h;i++){
    scanf("%s",M[i]);
  }
  static int K[1000][1001]={0};
  int dx[8]={1,1,1,0,0,-1,-1,-1},dy[8]={-1,0,1,-1,1,-1,0,1};
  queue<pair<int,int> > Q[2];
  bool b=0;
  for(int i=1;i<h-1;i++){
    for(int j=1;j<w-1;j++){
      for(int v=0;v<8;v++){
	int x=i+dx[v],y=j+dy[v];
	if(M[x][y]=='.'){
	  K[i][j]++;
	}
      }
      if(M[i][j]!='.'&&K[i][j]>=M[i][j]-'0'){
	Q[b].push(make_pair(i,j));
      }
    }
  }
  int t=0;
  while(!Q[b].empty()){
    while(!Q[b].empty()){
      pair<int,int> p=Q[b].front();
      Q[b].pop();
      int i=p.first,j=p.second;
      for(int v=0;v<8;v++){
	int x=i+dx[v],y=j+dy[v];
	K[x][y]++;
	if(M[x][y]!='.'&&K[x][y]==M[x][y]-'0'){
