          c++;
      }
      if(c < m[y][x])
        continue;
      bks.push(x);
      bks.push(y);
    }
    if(bks.empty())
      break;
    while(!bks.empty())
    {
      int x = bks.front(); bks.pop();
      int y = bks.front(); bks.pop();
      if(m[y][x] == -1)
        continue;
      m[y][x] = -1;
      for(int i = 0; i < 8; i++)
      {
        int tx = x + dx[i], ty = y + dy[i];
        if(m[ty][tx] != -1)
        {
          upp2->push(tx);
          upp2->push(ty);
        }
      }
    }

    ans += 1;
  }
  cout << ans << endl;
}

#include <iostream>
#include <string>
#include <queue>
#include <vector>

using namespace std;

typedef pair<int,int> P;

bool check_destroy(vector<vector<int> >& mp, int h, int w) {
    int cnt=0;
    int dh[8]={-1,-1,-1,0,1,1,1,0};
    int dw[8]={-1,0,1,1,1,0,-1,-1};

    for(int i=0; i<8; ++i) {
        cnt+=(mp[h+dh[i]][w+dw[i]]==0?1:0);
    }

    return mp[h][w]<=cnt;
}

bool check_stable(vector<vector<int> >& mp, int h, int w) {
    if(mp[h][w]==9) return true;

    int cnt=0;
    int dh[8]={-1,-1,-1,0,1,1,1,0};
    int dw[8]={-1,0,1,1,1,0,-1,-1};

    for(int i=0; i<8; ++i) {
        cnt+=(mp[h+dh[i]][w+dw[i]]==9?1:0);
    }

    return mp[h][w]+cnt>8;
}

int main()
{
    int H,W;
    cin >> H >> W;

    queue<P> q[2];
    queue<P> dest[2];

    vector<vector<int> > mp(H);
    int ans=0;

    for(int i=0; i<H; ++i) mp[i].resize(W);

    for(int i=0; i<H; ++i) {
        string instr;
        cin >> instr;
        for(int j=0; j<W; ++j) {
            switch(instr[j]) {
            case '.' :
                mp[i][j]=0;
                break;
            default :
                mp[i][j]=instr[j]-'0';
                break;
            }
        }
    }

    for(int i=1; i<H-1; ++i) {
        for(int j=1; j<W-1; ++j) {
            if(!check_stable(mp,i,j)) q[ans%2].push(P(i,j));
        }
    }

    while(1) {
        int QCNT=q[ans%2].size();

        while(!q[ans%2].empty()) {
            P temp=q[ans%2].front(); q[ans%2].pop();
            if(check_destroy(mp,temp.first,temp.second)) dest[ans%2].push(temp);
            else q[(ans+1)%2].push(temp);
        }

        while(!dest[ans%2].empty()) {
            P temp=dest[ans%2].front(); dest[ans%2].pop();
            mp[temp.first][temp.second]=0;
        }

        if(QCNT==q[(ans+1)%2].size()) break;
        ++ans;
    }

    cout << ans << endl;

    return 0;
}

