bool inq[1002][1002] = {};
char str[1001];

const int dir[9] = { -1,-1,0,1,1,0,-1,1,-1 };
inline bool check(int i,int j)
{
	if (castle[i][j] == 0)return false;
	int c = 0;
	for (int k = 0; k < 8; ++k)
	{
		if (castle[i + dir[k]][j + dir[k + 1]] == 0)++c;
	}
	if (c >= castle[i][j])
	{
		inq[i][j] = true;
		return true;
	}
	return false;
}

int main()
{
	scanf("%d %d", &h, &w);
	for (int i = 1; i <= h; ++i)
	{
		scanf("%s",str);
		for (int j = 1; j <= w; ++j)
		{
			castle[i][j] = ((str[j - 1] == '.') ? 0 : (str[j - 1] - '0'));
			if (castle[i][j] == 0)
			{
				nowque.emplace(i, j);
			}
		}
	}
	int time = 0;
	for (; true; ++time)
	{
		while (!nowque.empty())
		{
			pint x = nowque.front(); nowque.pop();
			for (int k = 0; k < 8; ++k)
			{
				if (!inq[x.first + dir[k]][x.second + dir[k + 1]])
				{
					if (check(x.first + dir[k], x.second + dir[k + 1]))
					{
						nexque.emplace(x.first + dir[k], x.second + dir[k + 1]);
					}
				}
			}
		}
		nowque = nexque;
		while (!nexque.empty())
		{
			pint y = nexque.front(); nexque.pop();
			castle[y.first][y.second] = 0;
		}
		if (nowque.empty())break;
	}
	printf("%d\n",time);
	return 0;
}

