int solve(int r, int s) {
	if (r <= 0 || s <= 0 || r > H || s > W)return 0; int B = 0;
	for (int i = -1; i <= 1; i++) { for (int j = -1; j <= 1; j++) { if (x[r + i][s + j] == 0)B++; } }
