const int d[] = {0, 1, 1, 0, -1, 1, -1, -1, 0};

int main(){
  int h, w;
  scanf("%d%d", &h, &w);
  static int cell[1123][1123], stat[1123][1123];
  REP(i, h) REP(j, w){
    while(isspace(cell[i][j] = getchar()));
    if(cell[i][j] == '.'){
      cell[i][j] = -1;
      stat[i][j] = -1;
    }else{
      cell[i][j] -= '0';
      stat[i][j] = 0;
    }
  }

  /*REP(i, h){
    REP(j, w) putchar(cell[i][j]);
    putchar('\n');
  }*/

  set<P> next;
  REP(i, h) REP(j, w) next.insert(mp(i, j));

  for(int t = 0;; ++t){
    set<P> next_;
    vector<P> ochita;
    for(auto i : next){
      if(stat[i.fi][i.se] != -1){
        int count = 0;
        REP(j, 8){
          if(stat[i.fi + d[j]][i.se + d[j + 1]] == -1) ++count;
        }
        if(count >= cell[i.fi][i.se]){
          //stat[i.fi][i.se] = -1;
          ochita.pb(mp(i.fi, i.se));
          REP(k, 8){
            next_.insert(mp(i.fi + d[k], i.se + d[k + 1]));
          }
        }
      }
    }
    for(auto i : ochita) stat[i.fi][i.se] = -1;


    if(next_.empty()){
      printf("%d\n", t);
      return 0;
    }else{
      next = next_;
    }

  }
}

