char masu[1003][1003];
int check[1003][1003]={0};
main(){
  int h,w;
  queue<P> q;
  queue<P> stacking;
  int nowx,nowy;
  P p,a;
  cin>>h>>w;
  for(int i=0;i<=h+1;i++){
    for(int j=0;j<=w+1;j++){
      if(i==0||j==0||i==h+1||j==w+1){
	kyoudo[i][j]=0;
	masu[i][j]='.';
	p.first=i;
	p.second=j;
	q.push(p);
	check[i][j]=1;
      }
      else{
	cin>>masu[i][j];
	if(masu[i][j]!='.'){
	  kyoudo[i][j]=masu[i][j]-'0';
	}
	else{
	  kyoudo[i][j]=0;
	  p.first=i;
	  p.second=j;
	  q.push(p);
	  check[i][j]=1;
	}
      }
    }
  }
  int count=0;
  while(!q.empty()){
    while(!q.empty()){
      p=q.front();
      nowx=p.first;
      nowy=p.second;
      for(int k=0;k<8;k++){
	int nx,ny;
	nx=nowx+dx[k];
