	    stacking.push(a);
	    check[nx][ny]=1;
	  }
	}
      }
      check[nowx][nowy]=1;
      q.pop();
    }
    while(!stacking.empty()){
      p=stacking.front();
      masu[p.first][p.second]='.';
      q.push(stacking.front());
      stacking.pop();
    }
    count=count+1;
  }
  cout<<count-1<<endl;
