using namespace std;
typedef pair<int, int>P;
typedef long long ll;

int h, w;
char ca[1000][1001];
char cpy[1000][1001];
int cnt[1000][1000];
bool b = 0;
queue<P> que[2];

int main(){
	scanf("%d%d", &h, &w);
	rep(i, h)
		scanf("%s", ca[i]);
	rep(i, h){
		rep(j, w){
			if (ca[i][j] == '.')continue;
			for (int t = -1; t <= 1; t++)
				for (int k = -1; k <= 1; k++)
					if (ca[i + t][j + k] == '.')
						cnt[i][j]++;
			if (cpy[i][j] != '.'&&ca[i][j] - '0' <= cnt[i][j]){
				cpy[i][j] = '.';
				que[b].push(P(i, j));
			}
		}
	}
	int res = 0;
	while (!que[b].empty()){
		res++;
		while (!que[b].empty()){
			P p = que[b].front(); que[b].pop();
			int f = p.first, s = p.second;
			for (int i = -1; i <= 1; i++){
				for (int j = -1; j <= 1; j++){
					int t = f + i, k = s + j;
					if (ca[t][k] == '.')continue;
					cnt[t][k]++;
					if (cpy[t][k] != '.'&&ca[t][k] - '0' <= cnt[t][k]){
						cpy[t][k] = '.';
						que[!b].push(P(t, k));
					}
			 	}
			}
		}
		b = !b;
	}
	printf("%d\n", res );
	return 0;
}

#include<bits/stdc++.h>
#define mp make_pair
using namespace std;
typedef pair<int, int> pii;
typedef pair<int, pii> piii;//cnt, coodinate
string grid[1111];
int neiber[1111][1111];
int dx[] = {0, 1, 1,  1,  0, -1, -1, -1};
int dy[] = {1, 1, 0, -1, -1, -1,  0,  1};
int w, h;
bool renge(int x, int y){
  if(0 <= x && x < w &&
     0 <= y && y < h)return true;
  return false;
}
bool del(int x, int y){
  int cnt = 0;
  if(!(0 <= x && x < w &&
       0 <= y && y < h))return false;
  if(grid[y][x] == '.') return false;
  for (int i = 0; i < 8; i++) {
    int nx = x + dx[i], ny = y + dy[i];
    if(!(0 <= nx && nx < w &&
         0 <= ny && ny < h))continue;
    if(grid[ny][nx] == '.')cnt++;
  }
  char tmp = grid[y][x];
  neiber[y][x] = cnt;
  if(cnt >= atoi(&tmp))return true;
  else return false;
}

int main(int argc, char *argv[]){
  queue<piii> que;
  queue<pii> p;
  cin >> h >> w;
  for (int i = 0; i < h; i++) {
    cin >> grid[i];
  }
  for (int i = 0; i < h; i++) {
    for (int j = 0; j < w; j++) {
      if(del(j, i)){
        p.push(mp(j, i));
        que.push(mp(1, mp(j, i)));
      }
    }
  }
  int ans = 0;
  while(!que.empty()){
    int cnt = que.front().first;
    if(ans != cnt){
      while(!p.empty()){
        int x = p.front().first;
        int y = p.front().second;
        p.pop();
        if(grid[y][x] == '.')continue;
        grid[y][x] = '.';
        for (int i = 0; i < 8; i++) {
          int nx = x + dx[i], ny = y + dy[i];
          if(!renge(nx, ny))continue;
          neiber[ny][nx]++;
        }
      }
    }
    int x = que.front().second.first;
    int y = que.front().second.second;
    if(grid[y][x] == '.')continue;
    que.pop();
    ans = cnt;
    for (int i = 0; i < 8; i++) {
      int nx = x + dx[i], ny = y + dy[i];
      if(!renge(nx, ny))continue;
      char tmp = grid[ny][nx];
      if(neiber[ny][nx] >= atoi(&tmp) && grid[ny][nx] != '.'){
        p.push(mp(nx, ny));
        que.push(mp(cnt + 1, mp(nx, ny)));
      }
    }
  }
  std::cout << ans << std::endl;
  return 0;
}

