using namespace std;

typedef pair<int, int> P;

int main(){

	int H, W;

	cin >> H >> W;

	int ans = 0;

	string map[1001];

	for(int i = 0; i < H; i++){
		cin >> map[i];
	}

	queue<P> que, que_empty;

	for(int i = 1; i < H - 1; i++){
		for(int j = 1; j < W - 1; j++){
			if(map[i][j] == '.'){
				continue;
			}
			int num = map[i][j] - '0';
			int count = 0;
			for(int x = -1; x <= 1; x++){
				for(int y = -1; y <= 1; y++){
					if(x == 0 && y == 0){
						continue;
					}
					if(map[i + y][j + x] == '.'){
						count++;
					}
				}
			}
			if(count >= num){
				P p;
				p.first = i;
				p.second = j;
				que.push(p);
			}
		}
	}

	for(int i = 0; i < que.size(); i++){
		P p = que.front();
		que.pop();
		map[p.first][p.second] = '.';
		que.push(p);
	}
	/*
	for(int i = 0; i < H; i++){
		cout << map[i] << endl;
	}
	*/
	while(!que.empty()){
		ans++;
		queue<P> que_next = que_empty;
		while(!que.empty()){
			P p = que.front();
			que.pop();

			for(int i = p.first - 1; i <= p.first + 1; i++){
				for(int j = p.second - 1; j <= p.second + 1; j++){
					if(map[i][j] == '.'){
						continue;
					}
					int num = map[i][j] - '0';
					int count = 0;
					for(int x = -1; x <= 1; x++){
						for(int y = -1; y <= 1; y++){
							if(x == 0 && y == 0){
								continue;
							}
							if(map[i + y][j + x] == '.'){
								count++;
							}
						}
					}
					if(count >= num){
						P p;
						p.first = i;
						p.second = j;
						que_next.push(p);
					}
				}
			}
		}

		que = que_next;

		for(int i = 0; i < que.size(); i++){
			P p = que.front();
			que.pop();
			if(map[p.first][p.second] != '.'){
				map[p.first][p.second] = '.';
			}
			que.push(p);
		}
		/*
		for(int i = 0; i < H; i++){
			cout << map[i] << endl;
		}
		*/
	}

	cout << ans << endl;

	return 0;
}

#include<iostream>
#include<queue>
using namespace std;
typedef pair<int,int> PA;
typedef pair<PA,int> PPA;
int main(){
  int h,w;
  char masu[1001][1001];
  int suji[1001][1001]={};
  queue<PPA> que;
  PA p;
  int sum = 0;
  cin >> h >> w;
  for(int i=0;i<h;i++)
    for(int j=0;j<w;j++){
      cin >> masu[i][j];
      if(masu[i][j] == '.'){
	for(int k=-1;k<=1;k++)
	  for(int l=-1;l<=1;l++){
	    if(k==0 && l==0) continue;
	    int y = i+k;
	    int x = j+l;
	    if(y<0||y>=h||x<0||x>=w||masu[y][x]=='.') continue;
	    suji[y][x]++;
	  }
      }
    }
  for(int i=0;i<h;i++)
    for(int j=0;j<w;j++){
      if(suji[i][j] >= masu[i][j]-'0' && masu[i][j]!='.'){
	que.push(PPA(PA(i,j),1));
      }
    }
  queue<PA> queb;
  while(!que.empty()){
    p = que.front().first;
    if(sum != que.front().second){
      while(!queb.empty()){
	masu[queb.front().first][queb.front().second] = '.';
	queb.pop();
      }
      sum++;
    }
    que.pop();
    queb.push(p);
    for(int k=-1;k<=1;k++)
      for(int l=-1;l<=1;l++){
	if(k==0 && l==0) continue;
	int y = p.first+k;
	int x = p.second+l;
	if(y<0||y>=h||x<0||x>=w||masu[y][x]=='.') continue;
	suji[y][x]++;
	if(suji[y][x] == masu[y][x]-'0'){
	  que.push(PPA(PA(y,x),sum+1));
	}
      }
  }
  cout << sum << endl;
  return (0);
}

