using namespace std;
#define INF 1000000000
typedef pair<int,int> P;
typedef long long int ll;

int main(){
	int h,w;
	char mas[1001][1001];
	cin >> h >> w;
	queue<P> que1,que2;
	for(int i=0;i<h;i++){
		for(int j=0;j<w;j++){
			cin >> mas[i][j];
			if(mas[i][j]!='.' && mas[i][j]!='9'){
				que1.push(P(j,i));
			}
		}
	}
	int ans = 0;
	while(1){
		while(!que1.empty()){
			P p = que1.front(); que1.pop();
			int x = p.first;
			int y = p.second;
			int power = mas[y][x] - '0';
			int cou = 0;
			for(int i=-1;i<=1;i++){
				if(power == cou) break;
				for(int j=-1;j<=1;j++){
					if(power == cou) break;
					int nx = x+i;
					int ny = y+j;
					if(mas[ny][nx]=='.'){
						cou++;
					}
				}
			}
			if(power == cou){
				que2.push(P(x,y));
			}
		}
		if(que2.empty()) break;
		ans++;
		while(!que2.empty()){
			P p = que2.front(); que2.pop();
			int x = p.first;
			int y = p.second;
			mas[y][x] = '.';
			for(int i=-1;i<=1;i++){
				for(int j=-1;j<=1;j++){
					int nx = x+i;
					int ny = y+j;
					if(mas[ny][nx]!='.' && mas[ny][nx]!='9'){
						que1.push(P(nx,ny));
					}
				}
			}
		}
	}
	cout << ans << endl;
}

