								continue;
							}
							if(map[i + y][j + x] == '.'){
								count++;
							}
						}
					}
					if(count >= num){
						P p;
						p.first = i;
						p.second = j;
						que_next.push(p);
					}
				}
			}
		}

		que = que_next;

		for(int i = 0; i < que.size(); i++){
			P p = que.front();
			que.pop();
			if(map[p.first][p.second] != '.'){
				map[p.first][p.second] = '.';
