typedef pair<LL, LL> PP;

LL d[10010]; //?????¢
vector<edge> vec[10010];

int last = 0;

LL dijkstra() {
	for (int i = 0; i < 10010; i++)d[i] = LINF;
	priority_queue<PP, std::vector<PP>, std::greater<PP>> pq;
	d[0] = 0;
	pq.push(make_pair(d[0], 0));//cost,to
	while (!pq.empty()) {
		PP p = pq.top();
		pq.pop();
		int i = p.second;
		if (d[i] < p.first)continue;
		for (int k = 0; k < vec[i].size(); k++) {
			edge e = vec[i][k];
			if (d[e.to] <= d[i] + e.cost)continue;
			if (e.to == n - 1)last = e.cost;
			d[e.to] = d[i] + e.cost;
			pq.push(make_pair(d[e.to], e.to));
		}
	}
	return d[n - 1];
}

