typedef pair<int,int> P;

int dx[] = {-1,-1,-1,0,0,1,1,1},dy[] = {-1,0,1,-1,1,-1,0,1};

int main(){
	int h,w,cnt[1000][1000] = {};
	string field[1000];
	cin >> h >> w;
	for(int i = 0;i < h;i++) cin >> field[i];
	set<P> next;
	for(int i = 0;i < h;i++){
		for(int j = 0;j < w;j++){
			if(field[i][j] == '.') continue;
			next.insert(P(i,j));
			for(int k = 0;k < 8;k++){
				int nx = i + dx[k],ny = j + dy[k];
				if(nx >= 0 && nx < h && ny >= 0 && ny < w && field[nx][ny] == '.') cnt[i][j]++;
			}
		}
	}
	for(int res = 0;;res++){
		bool chg = false;
		vector<P> chgs;
		for(auto it = next.begin();it != next.end();it++){
			P p = *it;
			int i = p.first,j = p.second;
			if(field[i][j] != '.' && cnt[i][j] >= field[i][j] - '0'){
				field[i][j] = '.';
				chg = true;
				for(int k = 0;k < 8;k++){
					int nx = i + dx[k],ny = j + dy[k];
					if(nx >= 0 && nx < h && ny >= 0 && ny < w && field[nx][ny] != '.') {
						chgs.push_back(P(nx,ny));
					}
				}
			}
		}
		next.clear();
		for(int i = 0;i < chgs.size();i++){
			int x = chgs[i].first,y = chgs[i].second;
			cnt[x][y]++;
			next.insert(P(x,y));
		}
		if(!chg){
			cout << res << endl;
			break;
		}
	}
	return 0;
}

