using P = pair<int, int>;

vector<P> next(int y, int x) {return {{y-1, x-1}, {y-1, x}, {y-1, x+1}, {y, x-1}, {y, x+1}, {y+1, x-1}, {y+1, x}, {y+1, x+1}};}

int main() {
  int H, W;
  cin >> H >> W;

  auto valid = [&](auto y, auto x) {return 0 <= y && y < H && 0 <= x && x < W;};

  vector<vector<int>> hp(H, vector<int>(W)), flat(H, vector<int>(W));

  for(auto i = 0; i < H; ++i) for(auto j = 0; j < W; ++j) {
    char c;
    cin >> c;
    hp[i][j] = (c == '.') ? 0 : (c - '0');
  }

  queue<P> cur, nex;
  for(auto i = 0; i < H; ++i) for(auto j = 0; j < W; ++j) if(hp[i][j] <= flat[i][j]) cur.emplace(i, j);
  for(auto step = 0; ; ++step) {
    if(cur.empty()) {cout << step - 1 << endl; break;}
    while(!cur.empty()) {
      int y, x;
      tie(y, x) = cur.front();
      cur.pop();
      for(auto p: next(y, x)) if(valid(p.first, p.second)) if(++flat[p.first][p.second] == hp[p.first][p.second]) nex.emplace(p);
    }
    swap(cur, nex);
  }
}

