#include <iostream>
#include <cstdio>
#include <algorithm>
#include <queue>
using namespace std;

int w,h,field[1000][1000],copied[1000][1000];

void show(){
	for(int i = 0;i < h;i++){
		for(int j = 0;j < w;j++){
			cout << field[i][j];
		}
		cout << endl;
	}
	cout << endl;
}

void copyarray(){
	for(int i = 0;i < h;i++){
		for(int j = 0;j < w;j++){
			field[i][j] = copied[i][j];
		}
	}
}

int around(int a,int b){
	int cnt = 0;
	for(int i = max(0,a - 1);i < min(h,a + 2);i++){
		for(int j = max(0,b - 1);j < min(w,b + 2);j++){
			if(!(a == i && b == j) && field[i][j] == 0) cnt++;
		}
	}
	return cnt;
}

int main(){
	queue<pair<int,int> > que1,que2;
	cin >> h >> w;
	getchar();
	for(int i = 0;i < h;i++){
		for(int j = 0;j < w;j++){
			char c = getchar();
			if(c == '.') copied[i][j] = 0;
			else copied[i][j] = c - '0';
		}
		getchar();
	}
	int chg = -1,cnt = -1;
	while(chg){
		copyarray();
		cnt++;
		if(chg == -1){
			chg++;
			for(int i = 0;i < h;i++){
				for(int j = 0;j < w;j++){
					if(field[i][j] != 0 && around(i,j) >= field[i][j]) {
						copied[i][j] = 0;
						chg++;
						que2.push(make_pair(i,j));
					}
				}
			}
		}else{
			chg = 0;
			que1 = que2;
			while(!que2.empty()) que2.pop();
			while(!que1.empty()){
				pair<int,int> p = que1.front();que1.pop();
				for(int i = max(0,p.first - 1);i < min(h,p.first + 2);i++){
					for(int j = max(0,p.second - 1);j < min(w,p.second + 2);j++){
						if(field[i][j] > 0 && around(i,j) >= field[i][j]) {
							field[i][j] = -1;
							copied[i][j] = 0;
							chg++;
							que2.push(make_pair(i,j));
						}
					}
				}
			}
		}
	}
	cout << cnt << endl;
	return 0;
}

