#include <bits/stdc++.h>
using namespace std;
typedef pair<int, int> P;

const int dxy[] = {-1, 0, 1};

char sand[1024][1024];
char tmp[1024][1024];

bool check(int y, int x)
{
    int cnt = 0;

    for (int i = 0; i < 3; i++){
        for (int j = 0; j < 3; j++){
            cnt += (sand[y + dxy[i]][x + dxy[j]] == '.');
        }
    }

    return (sand[y][x] - '0' <= cnt);
}

int main()
{
    int h, w;

    scanf("%d %d", &h, &w);
    for (int i = 0; i < h; i++) scanf("%s", sand[i]);

    queue<P> que;
    int cnt = 0;
    memcpy(tmp, sand, 1024 * 1024);
    for (int i = 0; i < h; i++){
        for (int j = 0; j < w; j++){
            if (sand[i][j] != '.' && check(i, j)){
                tmp[i][j] = '.';
                que.push(P(i, j));
            }
        }
    }
    memcpy(sand, tmp, 1024 * 1024);

    for (int size = que.size(); size; size = que.size()){
        cnt++;
        for (int i = 0; i < size; i++, que.pop()){
            P p = que.front();
            int y = p.first, x = p.second;

            for (int j = 0; j < 3; j++){
                for (int k = 0; k < 3; k++){
                    if (j == 1 && k == 1) continue;
                    int ty = y + dxy[j], tx = x + dxy[k];

                    if (sand[ty][tx] != '.' && check(ty, tx)){
                        tmp[ty][tx] = '.';
                        que.push(P(ty, tx));
                    }
                }
            }
        }
        memcpy(sand, tmp, 1024 * 1024);
    }

    printf("%d\n", cnt);

    return 0;
}

