void dfs(int x, int y, int depth)
{
	maxdepth = max(maxdepth, depth);

	if (sand[y][x] != '.')
	{
		int count = 0;

		for (int dir = 0; dir < 8; dir++)
		{
			if (sand[y + dy[dir]][x + dx[dir]] == '.')
			{
