			}
		}

		if (depth == 0)
		{
			if (count >= sand[y][x] - 48)
			{
				sand[y][x] = '.';

				for (int dir = 0; dir < 8; dir++)
				{
					dfs(x + dx[dir], y + dy[dir], 1);
				}
			}
