using P = std::pair<std::pair<int, int>, int>;
constexpr int MAX = 1000;

int h, w;
int board[MAX][MAX];
int dx[8] = { 0,-1,-1,-1,0,1,1,1 }, dy[8] = { -1,-1,0,1,1,1,0,-1 };

int count(int x, int y) {
	int cnt = 0;
	for (int i = 0; i < 8; ++i) {
		if (!board[y + dy[i]][x + dx[i]])
			++cnt;
	}
	return cnt;
}

int solve() {
	std::queue<P> que;

	for (int i = 0; i < h; ++i) {
		for (int j = 0; j < w; ++j) {
			if (board[i][j]) {
				if (board[i][j] <= count(j, i)) {
					que.push(P(std::make_pair(j, i), 0));
				}
			}
		}
	}

	int max = 0;

	std::vector<std::pair<int, int>> vec;

	while (!que.empty()) {
		P p = que.front(); que.pop();
		if (max < p.second) {
			for (auto& x : vec)
				board[x.second][x.first] = 0;
			vec.clear();
			max = p.second;

			for (int i = 0; i < h; ++i) {
				for (int j = 0; j < w; ++j)
					std::cout << board[i][j] << ' ';
				std::cout << std::endl;
			}

		}
		if (board[p.first.second][p.first.first] <= count(p.first.first, p.first.second)) {
			vec.push_back(std::make_pair(p.first.first, p.first.second));
			int nx, ny;
			for (int i = 0; i < 8; ++i) {
				nx = p.first.first + dx[i];
				ny = p.first.second + dy[i];
				if (board[ny][nx])
					que.push(P(std::make_pair(nx, ny), p.second + 1));
			}
		}
	}

	/*if (!vec.size())
		--max;*/

	return max;
}

int main() {
	std::cin >> h >> w;

	char ch;
	for (int i = 0; i < h; ++i) {
		for (int j = 0; j < w; ++j) {
			std::cin >> ch;
			if (std::isdigit(ch)) {
				board[i][j] = atoi(&ch);
			}
		}
	}

	std::cout << solve() << std::endl;

	return 0;
}

