    int y = p.first, x = p.second;

    res = max(res, d[y][x]);

    rep(i,8){
      int ny = y+dy[i], nx = x+dx[i];      
      if(!in_range(ny,0,h) || !in_range(nx,0,w))continue;

      if(g[ny][nx] != '.' && check(ny,nx,g[ny][nx]-'0',d,d[y][x])){
	g[ny][nx] = '.';
	d[ny][nx] = d[y][x] + 1;
	q.push(pii(ny,nx));
      }
    }
  }

  cout << res << endl;
}

