#include <cstring>
#include <iostream>
#include <algorithm>

using namespace std;

#define INF 2000000000

#define MAX_H 1050
#define MAX_W 1050

int H, W;

char sand[MAX_H][MAX_W];

int dp[MAX_H][MAX_W];

const int dx[8] = { -1, -1, -1, 0, 0, 1, 1, 1 };
const int dy[8] = { -1, 0, 1, -1, 1, -1, 0, 1 };

bool dfs[MAX_H][MAX_W];

void memo(int x, int y)
{
	dfs[y][x] = true;

	if (dp[y][x] > 1 && x > 0 && x < W - 1 && y > 0 && y < H - 1)
	{
		int temp[8];

		for (int dir = 0; dir < 8; dir++)
		{
			temp[dir] = dp[y + dy[dir]][x + dx[dir]];
		}

		sort(temp, temp + 8);

		if (sand[y][x] != '9')
		{
			if (temp[sand[y][x] - 49] != INF)
			{
				dp[y][x] = temp[sand[y][x] - 49] + 1;
			}

			if (dp[y][x] < INF)
			{
				for (int dir = 0; dir < 8; dir++)
				{
					if (dfs[y + dy[dir]][x + dx[dir]] == false)
					{
						memo(x + dx[dir], y + dy[dir]);
					}
				}
			}
		}
	}
}

int main()
{
	memset(dp, 0, sizeof(dp));

	cin >> H >> W;

	for (int i = 0; i < H; i++)
	{
		for (int j = 0; j < W; j++)
		{
			cin >> sand[i][j];

			if (sand[i][j] != '.')
			{
				dp[i][j] = INF;
			}
		}
	}

	for (int i = 1; i < H - 1; i++)
	{
		for (int j = 1; j < W - 1; j++)
		{
			if(dfs[i][j] == true)
			{
			    dfs[i][j] = false;
			}

			memo(j, i);
		}
	}

	int maxdepth = 0;

	for (int i = 1; i < H - 1; i++)
	{
		for (int j = 1; j < W - 1; j++)
		{
			if (dp[i][j] < INF)
			{
				maxdepth = max(dp[i][j], maxdepth);
			}
		}
	}

	cout << maxdepth << endl;

	return 0;
}

