typedef pair<int, int> P;

int h, w, ans;
int castle[1001][1001];
int times[1001][1001];
int dx[] = {-1, 0, 1, -1, 1, -1, 0, 1}, dy[] = {-1, -1, -1, 0, 0, 1, 1, 1};
char beach[1001][1001];

int main(){
    cin >> h >> w;
    for(int i = 1; i < h; i++){
        cin >> beach[i];
    }

    queue<P> Q;
    for(int i = 1; i < h; i++){
        for(int j = 1; j < w; j++){
            if(beach[i][j] == '.'){
                Q.push(P(i, j));
            } else{
                castle[i][j] = (int)beach[i][j] - 48;
            }
        }
    }

    while(Q.size()){
        P p = Q.front();
        Q.pop();
        ans = max(ans, times[p.first][p.second]);

        for(int i = 0; i < 8; i++){
            int x = p.first + dx[i], y = p.second + dy[i];
            castle[x][y]--;
            if(castle[x][y] == 0){
                Q.push(P(x, y));
                times[x][y] = times[p.first][p.second] + 1;
            }
        }
    }

    cout << ans << endl;

    return 0;
}

