typedef pair<int,int> pii;
int dx[] = {0,0,1,-1,1,1,-1,-1};
int dy[] = {1,-1,0,0,-1,1,1,-1};

queue<pii> q;

int main(){
  int h,w,sand[1000][1000];
  int sond[1000][1000];
  scanf("%d %d",&h,&w);
  for(int i=0;i<h;i++){
    for(int j=0;j<w;j++){
      char c;
      scanf(" %c",&c);
      if(c=='.') sand[i][j] = -10;
      else sand[i][j] = c-'0';
      if(c=='.') sond[i][j] = -10;
      else sond[i][j] = c-'0';
    }
  }
  q.push(pii(h,w));
  for(int i=1;i<h-1;i++){
    for(int j=1;j<w-1;j++){
      for(int k=0;k<8;k++) if(sand[i+dx[k]][j+dy[k]]<=-10) sand[i][j]--;
      if(sand[i][j]<=0&&sand[i][j]>-10) q.push(pii(i,j));
    }
  }
  int ans = 0;
  while(q.size()>1){
    int x = q.front().first;
    int y = q.front().second;
    q.pop();
    if(x==h&&y==w){
      ans++;
      q.push(pii(x,y));
      continue;
    }
    for(int i=0;i<8;i++){
      int nx = x+dx[i];
      int ny = y+dy[i];
      if(nx>=0&&nx<h&&ny>=0&&ny<w&&sand[nx][ny]>0){
        sand[nx][ny]--;
        if(sand[nx][ny]==0){
          q.push(pii(nx,ny));
        }
      }
    }
  }
  printf("%d\n",ans);
  return 0;
}

