#include <iostream>
#include <queue>
#include <tuple>
#include <utility>
#include <cstring>

using namespace std;

int h,w;
char bch[1003][1003];
bool done[1003][1003];

int wav (int i, int j)
{
    int c=0;
    if (bch[i-1][j-1]=='.') c++;
    if (bch[i-1][j]=='.') c++;
    if (bch[i-1][j+1]=='.') c++;
    if (bch[i][j-1]=='.') c++;
    if (bch[i][j+1]=='.') c++;
    if (bch[i+1][j-1]=='.') c++;
    if (bch[i+1][j]=='.') c++;
    if (bch[i+1][j+1]=='.') c++;
    return c;
}

int bfs ()
{
    int cnt, ans=0;
    queue<tuple<int, int, int>> que;
    vector<pair<int, int>> vec;
    for (int i=1; i<h-1; i++)
    {
        for (int j=1; j<w-1; j++)
        {
            if (bch[i][j]=='.') continue;
            cnt=wav(i, j);
            if (cnt>=bch[i][j]-'0') que.push(make_tuple(1, i, j));
        }
    }
    while (!que.empty())
    {
        int a=get<0>(que.front()), b=get<1>(que.front()), c=get<2>(que.front());
        que.pop();
        if (bch[b][c]=='.') continue;
        if (a>ans)
        {
            ans=a;
            for (auto u : vec)
            {
                int i=u.first, j=u.second;
                bch[i][j]='.';
            }
            vec.clear();
            memset(done, false, sizeof(done));
        }
        done[b][c]=true;
        cnt=wav(b, c);
        if (cnt<bch[b][c]-'0') continue;
        vec.push_back({b,c});
        if (bch[b-1][c-1]!='.' && !done[b-1][c-1]) que.push(make_tuple(a+1, b-1, c-1));
        if (bch[b-1][c]!='.' && !done[b-1][c]) que.push(make_tuple(a+1, b-1, c));
        if (bch[b-1][c+1]!='.' && !done[b-1][c+1]) que.push(make_tuple(a+1, b-1, c+1));
        if (bch[b][c-1]!='.' && !done[b][c-1]) que.push(make_tuple(a+1, b, c-1));
        if (bch[b][c+1]!='.' && !done[b][c+1]) que.push(make_tuple(a+1, b, c+1));
        if (bch[b+1][c-1]!='.' && !done[b+1][c-1]) que.push(make_tuple(a+1, b+1, c-1));
        if (bch[b+1][c]!='.' && !done[b+1][c]) que.push(make_tuple(a+1, b+1, c));
        if (bch[b+1][c+1]!='.' && !done[b+1][c+1]) que.push(make_tuple(a+1, b+1, c+1));
    }
    return ans;
}

int main()
{
    cin>>h>>w;
    for (int i=0; i<h; i++) for (int j=0; j<w; j++) cin>>bch[i][j];
    cout<<bfs()-1<<'\n';
    return 0;
}

